mapper.select:
SELECT * FROM period_date_configuration
WHERE apply = true
AND department_idx = #{idx}

SELECT force_run
FROM configuration
WHERE department_idx = #{idx}

mapper.update:
UPDATE security_usb_details
SET registry_name = #{registryName},
    process_name  = #{processName},
    run_file_name = #{runFileName}
WHERE department_idx = #{idx}

UPDATE period_date_configuration
SET apply = 0
WHERE idx = ${old_idx};
UPDATE period_date_configuration
SET apply = 1
WHERE idx = ${new_idx};

UPDATE configuration
SET force_run    = ${value}
WHERE department_idx = #{UserIdx}

mapper.delete:
DELETE
FROM period_date_configuration
WHERE period = ${period}
  AND from_week = ${fromWeek}
  AND from_day = ${fromDay}
  AND to_week = ${toWeek}
  AND to_day = ${toDay}
