mapper.update:
UPDATE period_date_configuration
SET apply = 0
WHERE idx = ${old_idx};
UPDATE period_date_configuration
SET apply = 1
WHERE idx = ${new_idx};

mapper.delete:
DELETE
FROM period_date_configuration
WHERE period = ${period}
  AND from_week = ${fromWeek}
  AND from_day = ${fromDay}
  AND to_week = ${toWeek}
  AND to_day = ${toDay}
