mapper.delete:
DELETE
FROM manager
WHERE manager.id = #{value};
SET @COUNT = 0;
UPDATE manager
SET manager.idx = @COUNT := @COUNT + 1;

mapper.update:
UPDATE manager
SET password = #{password}
WHERE id = #{userId} AND department_idx = #{departmentIdx};

UPDATE manager
SET locked = 0
WHERE id = #{userId};

UPDATE manager
SET locked = locked + 1
WHERE id = #{userId} AND department_idx = #{departmentIdx};
