mapper.select:
SELECT *
FROM process
WHERE display_name = #{displayName}
  AND registry_item = #{registryItem}

mapper.insert:
INSERT INTO process (registry_item, display_name)
SELECT #{registryItem},
       #{displayName}
FROM DUAL
WHERE NOT EXISTS(SELECT * FROM process WHERE registry_item = #{registryItem} AND display_name = #{displayName});

mapper.delete:
DELETE
FROM process
WHERE display_name = #{displayName}
  AND registry_item = #{registryItem}
